        public LookUpDTO getFileInfo(int id, string strYear, string strMonth, string strHeadOfficeId, string strBranchOfficeId)
        {
            LookUpDTO objLookUpDTO = new LookUpDTO();

            string sql = "";
            sql = "SELECT " +
                 "FILE_NAME, " +
                 "DATA_FILE " +

                "FROM BANK_SHEET_UPLOAD WHERE TRAN_ID = '" + id + "' AND SALARY_YEAR = '" + strYear + "' AND SALARY_MONTH = '" + strMonth + "' AND head_office_id = '" + strHeadOfficeId + "' and branch_office_id = '" + strBranchOfficeId + "' ";

            OracleCommand objCommand = new OracleCommand(sql);
            OracleDataReader objDataReader;

            using (OracleConnection strConn = GetConnection())
            {

                objCommand.Connection = strConn;
                strConn.Open();
                objDataReader = objCommand.ExecuteReader();
                try
                {
                    while (objDataReader.Read())
                    {

                        objLookUpDTO.FileName = objDataReader["FILE_NAME"].ToString();
                        objLookUpDTO.FileDocuments = (byte[])objDataReader["DATA_FILE"];

                    }
                }
                catch (Exception ex)
                {
                    throw new Exception("Error : " + ex.Message);

                }

                finally
                {

                    strConn.Close();
                }

            }

            return objLookUpDTO;
        }



