        public DataTable GetSpecialShiftConfiguration(string headOfficeId, string branchOfficeId)
        {

            LookUpDTO objLookUpDTO = new LookUpDTO();
            LookUpDAL objLookUpDAL = new LookUpDAL();

            DataTable dt = new DataTable();

            string sql = "";
            sql = "SELECT " +

                " shift_configuration_id, " +
                " SHIFT_ID, " +
                " shift_name, " +

                " punch_start_time, " +
                " login_start_time, " +
                " login_grace_time, " +
                " login_end_time, " +
                " lunch_out_time, " +
                " lunch_in_time, " +
                " lunch_out_start_time, " +
                " lunch_out_end_time, " +
                " lunch_in_start_time, " +
                " lunch_in_end_time, " +
                " logout_start_time, " +
                " logout_time, " +
                " punch_end_time, " +
                " early_ot_start_time, " +
                " effective_date, " +
                " active_yn, " +
                " head_office_id, " +
                " branch_office_id " +
                "FROM VW_SPECIAL_SHIFT_CONFIGURATION where head_office_id = '" + headOfficeId + "' and branch_office_id = '" + branchOfficeId + "' ORDER BY shift_name asc";

            OracleCommand objCommand = new OracleCommand(sql);
            OracleDataAdapter objDataAdapter = new OracleDataAdapter(objCommand);
            using (OracleConnection strConn = GetConnection())
            {
                try
                {
                    objCommand.Connection = strConn;
                    strConn.Open();
                    objDataAdapter.Fill(dt);
                    dt.AcceptChanges();
                }
                catch (Exception ex)
                {

                    throw new Exception("Error : " + ex.Message);
                }

                finally
                {
                    strConn.Close();
                }
            }
            return dt;
        }
