        public string GetDirectoryPath()
        {

            LoginDTO objLoginDTO = new LoginDTO();
            string directory_path = string.Empty;
            //p_directory_path

            OracleCommand objOracleCommand = new OracleCommand("SP_GET_FILE_UPLOAD_DIR_PATH");
            objOracleCommand.CommandType = CommandType.StoredProcedure;

            objOracleCommand.Parameters.Add("p_directory_path", OracleDbType.Varchar2, 100).Direction = ParameterDirection.Output;

            using (OracleConnection strConn = GetConnection())
            {
                try
                {
                    objOracleCommand.Connection = strConn;
                    strConn.Open();
                    trans = strConn.BeginTransaction();
                    objOracleCommand.ExecuteNonQuery();
                    trans.Commit();
                    strConn.Close();
                    directory_path = objOracleCommand.Parameters["p_directory_path"].Value.ToString();
                }

                catch (Exception ex)
                {
                    throw new Exception("Error : " + ex.Message);
                }

                finally
                {
                    strConn.Close();
                }
            }
            return directory_path;
        }


