        public string saveSalaryPaymentDateInfo(LookUpDTO objLookUpDTO)
        {

            LoginDTO objLoginDTO = new LoginDTO();
            string strMsg = "";


            OracleCommand objOracleCommand = new OracleCommand("PRO_SALARY_PAYMENT_DATE_SETUP");

            objOracleCommand.CommandType = CommandType.StoredProcedure;

            if (objLookUpDTO.Year != "")
            {
                objOracleCommand.Parameters.Add("P_SALARY_YEAR", OracleDbType.Varchar2, ParameterDirection.Input).Value = objLookUpDTO.Year;

            }
            else
            {

                objOracleCommand.Parameters.Add("P_SALARY_YEAR", OracleDbType.Varchar2, ParameterDirection.Input).Value = null;
            }



            if (objLookUpDTO.Month != "")
            {
                objOracleCommand.Parameters.Add("P_SALARY_MONTH", OracleDbType.Varchar2, ParameterDirection.Input).Value = objLookUpDTO.Month;
            }
            else
            {

                objOracleCommand.Parameters.Add("P_SALARY_MONTH", OracleDbType.Varchar2, ParameterDirection.Input).Value = null;
            }

            if (objLookUpDTO.PaymentDate != "")
            {
                objOracleCommand.Parameters.Add("P_PAYMENT_DATE", OracleDbType.Varchar2, ParameterDirection.Input).Value = objLookUpDTO.PaymentDate;
            }
            else
            {

                objOracleCommand.Parameters.Add("P_PAYMENT_DATE", OracleDbType.Varchar2, ParameterDirection.Input).Value = null;
            }

            objOracleCommand.Parameters.Add("P_UPDATE_BY", OracleDbType.Varchar2, ParameterDirection.Input).Value = objLookUpDTO.UpdateBy;
            objOracleCommand.Parameters.Add("P_HEAD_OFFICE_ID", OracleDbType.Varchar2, ParameterDirection.Input).Value = objLookUpDTO.HeadOfficeId;
            objOracleCommand.Parameters.Add("P_BRANCH_OFFICE_ID", OracleDbType.Varchar2, ParameterDirection.Input).Value = objLookUpDTO.BranchOfficeId;


            objOracleCommand.Parameters.Add("P_MESSAGE", OracleDbType.Varchar2, 500).Direction = ParameterDirection.Output;

            using (OracleConnection strConn = GetConnection())
            {
                try
                {
                    objOracleCommand.Connection = strConn;
                    strConn.Open();
                    trans = strConn.BeginTransaction();
                    objOracleCommand.ExecuteNonQuery();
                    trans.Commit();
                    strConn.Close();
                    strMsg = objOracleCommand.Parameters["P_MESSAGE"].Value.ToString();
                }

                catch (Exception ex)
                {
                    throw new Exception("Error : " + ex.Message);

                }

                finally
                {

                    strConn.Close();
                }

            }

            return strMsg;


        }



