            }

            if (objLookUpDTO.BarcodeNumber != "")
            {
                objOracleCommand.Parameters.Add("P_BARCODE_NUMBER_ID", OracleDbType.Varchar2, ParameterDirection.Input).Value = objLookUpDTO.BarcodeNumber;

            }
            else
            {

                objOracleCommand.Parameters.Add("P_BARCODE_NUMBER_ID", OracleDbType.Varchar2, ParameterDirection.Input).Value = null;
            }



            if (objLookUpDTO.SizeName != "")
            {
                objOracleCommand.Parameters.Add("P_SIZE_NAME", OracleDbType.Varchar2, ParameterDirection.Input).Value = objLookUpDTO.SizeName;
            }
            else
            {

                objOracleCommand.Parameters.Add("P_SIZE_NAME", OracleDbType.Varchar2, ParameterDirection.Input).Value = null;
            }

            if (objLookUpDTO.OrderQty != "")
            {
                objOracleCommand.Parameters.Add("P_ORDER_QTY", OracleDbType.Varchar2, ParameterDirection.Input).Value = objLookUpDTO.OrderQty;

            }
            else
            {

                objOracleCommand.Parameters.Add("P_ORDER_QTY", OracleDbType.Varchar2, ParameterDirection.Input).Value = null;
            }



            if (objLookUpDTO.SizeWisePackingQTY != "")
            {
                objOracleCommand.Parameters.Add("P_QTY_PER_CUTTON", OracleDbType.Varchar2, ParameterDirection.Input).Value = objLookUpDTO.SizeWisePackingQTY;
            }
            else
            {

                objOracleCommand.Parameters.Add("P_QTY_PER_CUTTON", OracleDbType.Varchar2, ParameterDirection.Input).Value = null;
            }

            objOracleCommand.Parameters.Add("P_UPDATE_BY", OracleDbType.Varchar2, ParameterDirection.Input).Value = objLookUpDTO.UpdateBy;
            objOracleCommand.Parameters.Add("P_HEAD_OFFICE_ID", OracleDbType.Varchar2, ParameterDirection.Input).Value = objLookUpDTO.HeadOfficeId;
            objOracleCommand.Parameters.Add("P_BRANCH_OFFICE_ID", OracleDbType.Varchar2, ParameterDirection.Input).Value = objLookUpDTO.BranchOfficeId;


            objOracleCommand.Parameters.Add("P_MESSAGE", OracleDbType.Varchar2, 500).Direction = ParameterDirection.Output;

            using (OracleConnection strConn = GetConnection())
            {
                try
                {
                    objOracleCommand.Connection = strConn;
                    strConn.Open();
                    trans = strConn.BeginTransaction();
                    objOracleCommand.ExecuteNonQuery();
                    trans.Commit();
                    strConn.Close();
                    strMsg = objOracleCommand.Parameters["P_MESSAGE"].Value.ToString();
                }

                catch (Exception ex)
                {
                    throw new Exception("Error : " + ex.Message);

                }

                finally
                {

                    strConn.Close();
                }

            }

            return strMsg;


        }

